import { ReactNode } from 'react';
import { TrendingUp, TrendingDown, Minus } from 'lucide-react';

interface DashboardMetricProps {
  title: string;
  value: string;
  change: number;
  changeType: 'increase' | 'decrease' | 'neutral';
  icon?: ReactNode;
}

export function DashboardMetric({ title, value, change, changeType, icon }: DashboardMetricProps) {
  const getChangeColor = () => {
    switch (changeType) {
      case 'increase':
        return 'text-success-600';
      case 'decrease':
        return 'text-error-600';
      case 'neutral':
        return 'text-gray-500';
      default:
        return 'text-gray-500';
    }
  };

  const getChangeIcon = () => {
    switch (changeType) {
      case 'increase':
        return <TrendingUp className="h-4 w-4" />;
      case 'decrease':
        return <TrendingDown className="h-4 w-4" />;
      case 'neutral':
        return <Minus className="h-4 w-4" />;
      default:
        return null;
    }
  };

  return (
    <div className="bg-white rounded-lg shadow-sm p-6 transition-all duration-200 hover:shadow-md">
      <div className="flex items-center">
        {icon && <div className="mr-4">{icon}</div>}
        <div>
          <p className="text-sm font-medium text-gray-500">{title}</p>
          <div className="flex items-baseline">
            <p className="text-2xl font-semibold text-gray-900">{value}</p>
            {change !== 0 && (
              <p className={`ml-2 flex items-center text-sm ${getChangeColor()}`}>
                {getChangeIcon()}
                <span className="ml-1">{Math.abs(change)}%</span>
              </p>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

import { format, parseISO } from 'date-fns';
import { 
  ShoppingCart, 
  DollarSign, 
  Users, 
  FolderKanban, 
  HeartHandshake,
  Clock,
  CheckCircle,
  AlertCircle
} from 'lucide-react';

interface Activity {
  id: string;
  type: string;
  description: string;
  timestamp: string;
  user: string;
  module: string;
  status?: string;
}

interface DashboardRecentActivityProps {
  activities: Activity[];
}

export function DashboardRecentActivity({ activities }: DashboardRecentActivityProps) {
  const getActivityIcon = (type: string) => {
    switch (type) {
      case 'purchase_order':
        return <ShoppingCart className="h-5 w-5 text-primary-500" />;
      case 'invoice':
        return <DollarSign className="h-5 w-5 text-success-500" />;
      case 'employee':
        return <Users className="h-5 w-5 text-accent-500" />;
      case 'project':
        return <FolderKanban className="h-5 w-5 text-warning-500" />;
      case 'customer':
        return <HeartHandshake className="h-5 w-5 text-secondary-500" />;
      default:
        return <Clock className="h-5 w-5 text-gray-400" />;
    }
  };

  const getStatusBadge = (status?: string) => {
    if (!status) return null;

    switch (status) {
      case 'completed':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-success-100 text-success-800">
            <CheckCircle className="mr-1 h-3 w-3" />
            Completed
          </span>
        );
      case 'pending':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-warning-100 text-warning-800">
            <Clock className="mr-1 h-3 w-3" />
            Pending
          </span>
        );
      case 'in_progress':
        return (
          <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-primary-100 text-primary-800">
            <AlertCircle className="mr-1 h-3 w-3" />
            In Progress
          </span>
        );
      default:
        return null;
    }
  };

  const formatTime = (timestamp: string) => {
    try {
      const date = parseISO(timestamp);
      return format(date, 'MMM d, yyyy - h:mm a');
    } catch (e) {
      return timestamp;
    }
  };

  return (
    <ul className="divide-y divide-gray-200">
      {activities.map((activity) => (
        <li key={activity.id} className="py-4">
          <div className="flex items-start">
            <div className="flex-shrink-0 h-10 w-10 rounded-md bg-gray-100 flex items-center justify-center">
              {getActivityIcon(activity.type)}
            </div>
            <div className="ml-4 flex-1">
              <div className="flex items-center justify-between">
                <p className="text-sm font-medium text-gray-900">{activity.description}</p>
                {getStatusBadge(activity.status)}
              </div>
              <div className="mt-1 flex items-center text-sm text-gray-500">
                <p className="mr-4">{activity.user}</p>
                <p>{formatTime(activity.timestamp)}</p>
              </div>
              <p className="mt-1 text-xs text-gray-400">{activity.module}</p>
            </div>
          </div>
        </li>
      ))}
    </ul>
  );
}

